<script setup>
import { ref, onMounted, computed } from 'vue'
import { useRouter } from 'vue-router'
import { auth } from '../firebase'
import { signOut } from 'firebase/auth'
import { onAuthStateChanged, updateProfile } from 'firebase/auth'
import { doc, getDoc, updateDoc } from 'firebase/firestore'
import { db } from '../firebase'

const router = useRouter()


const loading = ref(true)
const saving = ref(false)
const error = ref('')

const firstName = ref('')
const lastName = ref('')
const email = ref('')
const phone = ref('')
const birthDay = ref('')
const birthMonth = ref('')
const birthYear = ref('')
const gender = ref('') 
const isEmployee = ref('hayir')

let userUid = ''

const startYear = 2008;
const days = Array.from({ length: 31 }, (_, i) => i + 1)
const months = Array.from({ length: 12 }, (_, i) => i + 1)
const years = Array.from({ length: 84 }, (_, i) => startYear - i)

// Helper to check if email/phone is filled for verification style
const isEmailVerified = computed(() => email.value && email.value.includes('@'))
const isPhoneVerified = computed(() => phone.value && phone.value.length > 5)

onMounted(() => {
  onAuthStateChanged(auth, async (user) => {
    if (!user) {
      router.push('/home')
      return
    }

    userUid = user.uid
    email.value = user.email || ''

    try {
      const refDoc = doc(db, 'users', userUid)
      const snap = await getDoc(refDoc)
      if (snap.exists()) {
        const data = snap.data()
        firstName.value = data.firstName || data.name || ''
        lastName.value = data.lastName || ''
        phone.value = data.phone || ''
        gender.value = data.gender || ''
        birthDay.value = data.birthDay || ''
        birthMonth.value = data.birthMonth || ''
        birthYear.value = data.birthYear || ''
        isEmployee.value = data.isEmployee || 'hayir'
      }
    } catch (e) {
      console.error(e)
      error.value = 'Bilgiler alınırken hata oluştu.'
    } finally {
      loading.value = false
    }
  })
})

const saveProfile = async () => {
  if (!userUid) return
  saving.value = true
  error.value = ''
  try {
    const refDoc = doc(db, 'users', userUid)
    await updateDoc(refDoc, {
      firstName: firstName.value,
      lastName: lastName.value,
      phone: phone.value,
      gender: gender.value,
      birthDay: birthDay.value,
      birthMonth: birthMonth.value,
      birthYear: birthYear.value,
      isEmployee: isEmployee.value,
    })

    if (auth.currentUser) {
      await updateProfile(auth.currentUser, {
        displayName: `${firstName.value} ${lastName.value}`.trim(),
      })
    }

    alert('Bilgiler kaydedildi.')
  } catch (e) {
    console.error(e)
    error.value = 'Kaydedilirken hata oluştu.'
  } finally {
    saving.value = false
  }
}

const deleteAccount = () => {
  alert('Bu projede hesap silme işlemi sadece görsel olarak gösterilmektedir.')
}

const logout = async () => {
  try {
    await signOut(auth)         
    router.push('/home')   
  } catch (e) {
    console.error('Logout error:', e)
  }
}


const giftOpen = ref(false)
const paymentOpen = ref(false)
const accountOpen = ref(false) 

const toggleGift = () => giftOpen.value = !giftOpen.value
const togglePayment = () => paymentOpen.value = !paymentOpen.value
const toggleAccount = () => accountOpen.value = !accountOpen.value

</script>

